import React, { useState } from "react";
import Searchbar from "./Searchbar";
import Header from "./Header";

export default function DictionaryCard() {
  const [searchResults, setSearchResults] = useState([]);

  async function handleSearch(keyword) {
    const apiURL = `https://api.dictionaryapi.dev/api/v2/entries/en/${keyword}`;

    try {
      const response = await fetch(apiURL);
      if (response.ok) {
        const data = await response.json();
        setSearchResults(data || []);
        console.log(data[0].phonetics[0].audio);
      } else {
        console.error("API request failed.");
      }
    } catch (error) {
      console.error("An error occurred  while fetching data", error);
    }
  }
  return (
    <div className="bg-white rounded-lg shadow-lg w-3/4 p-6">
      <Header />
      <h1 className="text-3xl font-semibold mb-4">Dictionary App</h1>
      <Searchbar onSearch={handleSearch} />

      {/* Display search results */}
      <div className="mt-4">
        {searchResults.length === 0 ? (
          <p className="text-gray-600">No results found.</p>
        ) : (
          <ul>
            {searchResults.map((result, index) => (
              <li key={index} className="mb-2">
                <p className="text-3xl font-bold p-4">{result.word}:</p>
                <p className="text-md font-normal text-[#f72585] p-4 italic ">
                  {result.phonetic}
                </p>
                <p>{result.phonetic}</p>
              </li>
            ))}
            {searchResults[0].phonetics?.map((phonetic, index) => (
              <div key={index}>
                <audio controls>
                  <source src={phonetic.audio} type="audio/mpeg"></source>
                </audio>
              </div>
            ))}
            {searchResults.map((meaning, index) => (
              <div key={index}>
                <p>{meanings.partOfSpeech}</p>
              </div>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
