import React from "react";
import { useState } from "react";

export default function Searchbar({ onSearch }) {
  const [keyword, setKeyword] = useState("");

  function handleInputChange(e) {
    setKeyword(e.target.value);
  }

  function handleSearch() {
    if (keyword.trim() !== "") {
      onSearch(keyword);
    }
  }
  return (
    <div className="flex items-center justify-center w-full">
      <input
        type="text"
        className="w-full px-4 py-2 border rounded-l-lg focus:outline-none focus:ring focus:border-blue-300"
        placeholder="Search for a word..."
        value={keyword}
        onChange={handleInputChange}
      />
      <button
        className="px-4 py-2 bg-[#f72585] text-white rounded-r-lg hover:bg-blue-600 focus:outline-none focus:ring focus:bg-blue-300"
        onClick={handleSearch}
      >
        Search
      </button>
    </div>
  );
}
