import React, { useEffect, useState } from "react";
import Searchbar from "./Searchbar";
import Header from "./Header";

export default function DictionaryCard({ wordData }) {
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    async function handleSearch(keyword) {
      const apiURL = `https://api.dictionaryapi.dev/api/v2/entries/en/${keyword}`;

      try {
        const response = await fetch(apiURL);

        if (response.ok) {
          const data = await response.json();
          if (Array.isArray(data) && data.length > 0) {
            const result = data[0];
            // console.log("result is: ", result);

            const word = result.word;
            const spelling = result.phonetic;

            const phonetix = result.phonetics;
            // console.log("Phonetic is: ", phonetix);

            const meaninx = result.meanings;
            // console.log("Meaning is: ", meaninx);

            const resultsArray = [{ word, spelling, phonetix, meaninx }];
            // console.log(resultsArray);
            setSearchResults(resultsArray);
            console.log(searchResults);
          } else {
            console.log("No results found.");
            setSearchResults([]);
          }
        } else {
          console.error("API request failed.");
        }
      } catch (error) {
        console.error("An error occurred  while fetching data", error);
      }
    }
  }, []);
  return (
    <div className="bg-white rounded-lg shadow-lg w-3/4 p-6">
      <Header />
      <h1 className="text-3xl font-semibold mb-4">Dictionary App</h1>
      <Searchbar onSearch={handleSearch} />

      {/* Display search results */}
      <div className="mt-4">
        {searchResults.length === 0 ? (
          <p className="text-gray-600">No results found.</p>
        ) : (
          <ul>
            {searchResults.map((result, index) => (
              <li key={index} className="mb-2">
                <p className="text-3xl font-bold p-4">{result.word}:</p>
                <p className="text-md font-normal text-[#f72585] p-4 italic ">
                  {result.phonetic}
                </p>
                {/* <p>{result.phonetic}</p> */}
              </li>
            ))}
            {searchResults.map((e, index) => (
              <div key={index}>
                <audio controls>
                  <source src={e} type="audio/mpeg"></source>
                </audio>
              </div>
            ))}
            {searchResults[0].meanings.map((meaning, index) => (
              <div key={index}>
                <p>{meaning.partOfSpeech}</p>
              </div>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
